import { memo, useMemo, FC } from "react";

import { TabData, UIElement, Addons } from "./element.types";
import { Groupbox } from "./elements/GroupBox";
import { TabContainer, TabLeft, TabRight } from "./elements/Tab";
import Divider from "./elements/Divider";
import Toggle from "./elements/Toggle";
import Button from "./elements/Button";
import ObsidianImage from "./elements/Image";
import Label from "./elements/Label";
import Tabbox from "./elements/TabBox";
import Dropdown from "./elements/Dropdown";
import Input from "./elements/Input";
import Slider from "./elements/Slider";
import KeyPicker from "./elements/addons/KeyPicker";
import AddonContainer from "./elements/addons/AddonContainer";
import ColorPicker from "./elements/addons/ColorPicker";
import ObsidianWarningBox from "./elements/WarningBox";

// Parsers //
const renderAddons = (
  element: UIElement,
  addons?: Addons[],
  stateKeyPrefix?: string
) => {
  if (!addons || addons.length === 0) return null;

  const scope = stateKeyPrefix || "global";
  return (
    <AddonContainer className="absolute inset-0 pointer-events-none">
      {addons.map((addon, idx) => {
        const addonKey = `${scope}:addon:${addon.type}:${element.index}:${idx}`;

        switch (addon.type) {
          case "KeyPicker":
            return (
              <KeyPicker
                key={idx}
                defaultValue={addon.value}
                className="pointer-events-auto"
                stateKey={addonKey}
              />
            );

          case "ColorPicker":
            return (
              <ColorPicker
                key={idx}
                title={addon.title}
                defaultValue={addon.value}
                className="pointer-events-auto"
                stateKey={addonKey}
              />
            );

          default:
            return null;
        }
      })}
    </AddonContainer>
  );
};

export const ElementParser: FC<{
  element: UIElement;
  stateKeyPrefix?: string;
}> = ({ element, stateKeyPrefix }) => {
  if ("visible" in element && !element.visible) return null;

  const scope = stateKeyPrefix || "global";
  const addons = (element as unknown as { properties?: { addons?: Addons[] } })
    .properties?.addons;
  const core = (() => {
    switch (element.type) {
      case "Toggle":
        return (
          <Toggle
            text={element.text}
            risky={element.properties.risky}
            checked={element.value}
            stateKey={`${scope}:el:Toggle:${element.index}`}
          />
        );

      case "Label":
        return <Label>{element.text}</Label>;

      case "Button":
        return <Button text={element.text} subButton={element.subButton} />;

      case "Dropdown":
        return (
          <Dropdown
            text={element.text}
            value={element.value}
            options={element.properties.values}
            multi={element.properties.multi === true}
            disabledValues={element.properties.disabledValues || []}
            stateKey={`${scope}:el:Dropdown:${element.index}`}
          />
        );

      case "Slider":
        return (
          <Slider
            text={element.text}
            value={element.value}
            min={element.properties.min}
            max={element.properties.max}
            compact={element.properties.compact}
            rounding={element.properties.rounding}
            prefix={element.properties.prefix}
            suffix={element.properties.suffix}
            stateKey={`${scope}:el:Slider:${element.index}`}
          />
        );

      case "Input":
        return (
          <Input
            text={element.text}
            value={element.value}
            placeholder={element.properties.placeholder}
            stateKey={`${scope}:el:Input:${element.index}`}
          />
        );

      case "Divider":
        return <Divider />;

      case "Image":
        return (
          <ObsidianImage
            image={element.properties.image}
            transparency={element.properties.transparency}
            scaleType={element.properties.scaleType}
            color={element.properties.color}
            rectOffset={element.properties.rectOffset}
            height={element.properties.height}
            rectSize={element.properties.rectSize}
          />
        );

      default:
        return (
          <div className="text-red-400 text-left">
            Unknown element type:{" "}
            {(element as { type: string }).type || "Unknown"}
          </div>
        );
    }
  })();

  return (
    <div className="relative">
      {core}
      {renderAddons(element, addons, stateKeyPrefix)}
    </div>
  );
};

const TabParserComponent: FC<{ tabData: TabData | null }> = ({ tabData }) => {
  const { groupboxes, tabboxes, warningBox } = tabData || {};

  const leftGroupboxes = useMemo(
    () =>
      groupboxes?.Left
        ? Object.values(groupboxes.Left).sort(
            (a, b) => (a.order ?? 0) - (b.order ?? 0)
          )
        : [],
    [groupboxes?.Left]
  );

  const rightGroupboxes = useMemo(
    () =>
      groupboxes?.Right
        ? Object.values(groupboxes.Right).sort(
            (a, b) => (a.order ?? 0) - (b.order ?? 0)
          )
        : [],
    [groupboxes?.Right]
  );

  const leftTabboxes = useMemo(
    () => (tabboxes?.Left ? Object.values(tabboxes.Left) : []),
    [tabboxes?.Left]
  );

  const rightTabboxes = useMemo(
    () => (tabboxes?.Right ? Object.values(tabboxes.Right) : []),
    [tabboxes?.Right]
  );

  if (!tabData) return null;

  return (
    <>
      {warningBox && (
        <ObsidianWarningBox
          text={warningBox.Text}
          title={warningBox.Title}
          visible={warningBox.Visible}
          isNormal={warningBox.IsNormal}
          lockSize={warningBox.LockSize}
        />
      )}
      <TabContainer>
        <TabLeft>
          {leftTabboxes.map((tabbox) => (
            <Tabbox
              key={tabbox.name}
              tabs={tabbox.tabs}
              scope={`tab:${tabData.name}:left:tabbox:${tabbox.name}`}
            />
          ))}
          {leftGroupboxes.map((gb) => (
            <Groupbox key={gb.name} title={gb.name}>
              {gb.elements.map((el) => (
                <ElementParser
                  key={`left-gb-${gb.name}-${el.index}`}
                  element={el}
                  stateKeyPrefix={`gb:${tabData.name}:left:groupbox:${gb.name}`}
                />
              ))}
            </Groupbox>
          ))}
        </TabLeft>
        <TabRight>
          {rightTabboxes.map((tabbox) => (
            <Tabbox
              key={tabbox.name}
              tabs={tabbox.tabs}
              scope={`tab:${tabData.name}:right:tabbox:${tabbox.name}`}
            />
          ))}
          {rightGroupboxes.map((gb) => (
            <Groupbox key={gb.name} title={gb.name}>
              {gb.elements.map((el) => (
                <ElementParser
                  key={`right-gb-${gb.name}-${el.index}`}
                  element={el}
                  stateKeyPrefix={`gb:${tabData.name}:right:groupbox:${gb.name}`}
                />
              ))}
            </Groupbox>
          ))}
        </TabRight>
      </TabContainer>
    </>
  );
};

TabParserComponent.displayName = "TabParser";
export const TabParser = memo(TabParserComponent);

"use client";

import React from "react";
import type { ReactNode } from "react";

type UIState = Record<string, unknown>;

type UIStateContextType = {
	state: Readonly<UIState>;
	setState: (key: string, value: unknown) => void;
	resetState: (prefix?: string) => void;
	subscribe: (key: string, callback: () => void) => () => void;
	get: (key: string) => unknown;
};

const UIStateContext = React.createContext<UIStateContextType | null>(null);

export function UIStateProvider({ children }: { children: ReactNode }) {
	const [state, setStateMap] = React.useState<UIState>({});
	const stateRef = React.useRef<UIState>({});

	const listenersRef = React.useRef<Map<string, Set<() => void>>>(new Map());
	const notify = React.useCallback((key: string) => {
		const set = listenersRef.current.get(key);
		if (!set) return;
		for (const cb of Array.from(set)) {
			try { cb(); }
			catch (err) {
				if (process.env.NODE_ENV !== "production") {
					// eslint-disable-next-line no-console
					console.error("[UIState.notify] listener threw:", err);
				}
			}
		}
	}, []);

	const setState = React.useCallback((key: string, value: unknown) => {
		const prev = (stateRef.current as Record<string, unknown>)[key];
		if (Object.is(prev, value)) return;

		const next = { ...stateRef.current, [key]: value };
		stateRef.current = next;
		setStateMap(next);
		notify(key);
	}, [notify]);

	const resetState = React.useCallback((prefix?: string) => {
		if (prefix === undefined) {
			// notify all keys //
			stateRef.current = {};
			setStateMap({});
			for (const key of listenersRef.current.keys()) notify(key);
			return;
		}

		const prev = stateRef.current;
		const next: UIState = {};
		for (const [k, v] of Object.entries(prev)) {
			if (!k.startsWith(prefix)) next[k] = v;
		}

		// notify keys under the prefix //
		stateRef.current = next;
		setStateMap(next);
		for (const key of listenersRef.current.keys()) {
			if (key.startsWith(prefix)) notify(key);
		}
	}, [notify]);

	const subscribe = React.useCallback((key: string, callback: () => void) => {
		let listeners = listenersRef.current.get(key);
		if (!listeners) {
			listeners = new Set();
			listenersRef.current.set(key, listeners);
		}

		listeners.add(callback);
		return () => {
			listeners?.delete(callback);
			if (listeners && listeners.size === 0) listenersRef.current.delete(key);
		};
	}, []);

	const get = React.useCallback((key: string) => stateRef.current[key], []);

	return (
		<UIStateContext.Provider value={{ state, setState, resetState, subscribe, get }}>
			{children}
		</UIStateContext.Provider>
	);
}

export function useUIState() {
	const ctx = React.useContext(UIStateContext);
	if (!ctx) throw new Error("useUIState must be used within UIStateProvider");
	return ctx;
}

export function useResetUIState() {
	const { resetState } = useUIState();
	return resetState;
}

export function useUIValue<T = unknown>(key: string | undefined, initialValue?: T): [T | undefined, (value: T | ((prev: T | undefined) => T)) => void] {
	const ctx = React.useContext(UIStateContext);
	if (!ctx) throw new Error("useUIValue must be used within UIStateProvider");

	const { setState, subscribe, get } = ctx;

	const getSnapshot = React.useCallback(() => {
		if (!key) return initialValue as T | undefined;
		return (get(key) as T | undefined) ?? (initialValue as T | undefined);
	}, [get, key, initialValue]);

	const getServerSnapshot = React.useCallback(() => initialValue as T | undefined, [initialValue]);

	const subscribeKey = React.useCallback((onStoreChange: () => void) => {
		if (!key) return () => { };
		return subscribe(key, onStoreChange);
	}, [subscribe, key]);

	const value = React.useSyncExternalStore(
		subscribeKey,
		getSnapshot as unknown as () => T | undefined,
		getServerSnapshot as unknown as () => T | undefined
	);

	const set = React.useCallback((v: T | ((prev: T | undefined) => T)) => {
		if (!key) return;
		const next = typeof v === "function"
			? (v as (prev: T | undefined) => T)((get(key) as T | undefined) ?? (initialValue as T | undefined))
			: v;
		setState(key, next as T);
	}, [setState, key, get, initialValue]);

	return [value, set];
}

export function useUIColor(key: string | undefined, initialColor?: { r: number; g: number; b: number }) {
	return useUIValue<{ r: number; g: number; b: number }>(key, initialColor);
}

export function useUIString(key: string | undefined, initialValue?: string) {
	return useUIValue<string>(key, initialValue);
}

import Image from "next/image";
import { MoveDiagonal2, Move, Search } from "lucide-react";
import { Tabs, TabsList, TabsTrigger, TabsContent } from "@/components/ui/tabs";
import React, { ReactNode } from "react";

import { cn } from "@/lib/utils";
import { TabParser } from "./DynamicTab";
import { UIData } from "./element.types";
import { IBMMono } from "./fonts";

import * as LucideIcons from "lucide-react";
type LucideIcon = React.ComponentType<React.ComponentProps<"svg">>;
const normalizeIconName = (name?: string) => {
  if (!name) return name;
  return name.endsWith("Icon") ? name.slice(0, -4) : name;
};
const getIcon = (name?: string) => {
  const key = normalizeIconName(name);
  return (
    (key && (LucideIcons as unknown as Record<string, LucideIcon>)[key]) || null
  );
};

interface ObsidianWindowProps {
  title: ReactNode | string;
  icon?: ReactNode | string;
  footer: ReactNode | string;
  uiData?: UIData;
  width?: number | string;
  height?: number | string;
}

export function ObsidianWindow({
  title,
  icon,
  footer,
  uiData,
  width,
  height,
}: ObsidianWindowProps) {
  const sortedTabs = React.useMemo(
    () =>
      uiData
        ? Object.entries(uiData.tabs).sort(([, a], [, b]) => a.order - b.order)
        : [],
    [uiData]
  );
  const defaultTab = sortedTabs[0]?.[0] || "Home";

  if (!uiData) return <p>Loading Features...</p>;
  const w = typeof width === "number" ? `${width}px` : width ?? "720px";
  const h = typeof height === "number" ? `${height}px` : height ?? "600px";
  return (
    <div
      className={cn(
        "rounded-[3px] bg-[rgb(15,15,15)] border-[rgb(40,40,40)] border relative font-normal stroke-white text-white",
        IBMMono.className
      )}
      style={{ width: w, height: h }}
    >
      <div className="w-full h-[48px] flex flex-row px-0 bg-[rbga(13,13,13,1)]">
        {/* Title */}
        <div className="flex flex-row items-center justify-center w-[30%] h-full gap-[3px] border-b-[rgb(40,40,40)] border-b">
          {typeof icon === "string" ? (
            <Image src={icon} width={30} height={30} alt="logo" />
          ) : (
            icon ?? null
          )}
          <span className="text-white text-sm ml-1">{title}</span>
        </div>

        <div className="w-[70%] h-full flex flex-row items-center gap-[3px] border-l-[rgb(40,40,40)] border-l border-b-[rgb(40,40,40)] border-b">
          <div className="absolute mt-0 left-[214px] w-[505px] h-[49px] flex items-center">
            {/* Searchbox */}
            <div className="flex items-center w-[449px] h-[34px] ml-[8px] mt-[1px] bg-[#191919] border border-[#272727] rounded px-2">
              <Search className="text-[#5f5f5f] mr-2" />
              <input
                type="text"
                placeholder="Search"
                aria-label="Search"
                className="w-full mt-[4px] text-center text-[13.5px] text-[#fff] bg-transparent placeholder-[#5f5f5f] outline-none"
              />
            </div>

            {/* Move Icon */}
            <Move className="text-[rgb(40,40,40)] ml-auto mr-auto" />
          </div>
        </div>
      </div>

      {/* Tabs */}
      <Tabs
        defaultValue={defaultTab}
        className="h-[calc(100%-69px)] w-full flex flex-row bg-[#111111] gap-0"
      >
        <TabsList className="h-full border-r-[rgb(40,40,40)] border-r min-w-[calc(30%+1.2px)] flex flex-col justify-start bg-[rgba(15,15,15,1)] rounded-none p-0">
          {sortedTabs.map(([tabName, tab], index) => {
            const IconTab = getIcon(tab.icon);
            return (
              <TabsTrigger
                value={tabName}
                key={index}
                className="flex flex-row items-center justify-start w-full max-h-[40px] min-h-[40px] border-b-[rgb(40,40,40)] border-b rounded-none py-[11px] px-[12px] data-[state=active]:bg-[rgb(25,25,25)] text-white text-opacity-75 data-[state=active]:text-white"
              >
                {IconTab && (
                  <IconTab className="text-[rgb(125,85,255)] h-full mr-2" />
                )}
                <span className="text-[13px]">{tabName}</span>
              </TabsTrigger>
            );
          })}
        </TabsList>

        {sortedTabs.map(([tabName, tab], index) => (
          <TabsContent
            value={tabName}
            key={index}
            className="flex flex-col w-full max-w-[calc(100%-30%+1.2px)] bg-[#111111] p-0 mt-0 overflow-hidden"
          >
            <TabParser tabData={tab} />
          </TabsContent>
        ))}
      </Tabs>

      {/* Footer */}
      <div className="h-[20px] w-full bg-[rgb(15,15,15)] border-t-[rgb(40,40,40)] border-t absolute bottom-0 flex flex-row items-center justify-center">
        <p className="text-[12px] text-white opacity-50">{footer}</p>

        <MoveDiagonal2 className="text-white opacity-50 w-[16px] h-[16px] absolute right-0 mr-[2px] pointer-events-none" />
      </div>
    </div>
  );
}

// Data Structures //
export interface Color3 {
  r: number;
  g: number;
  b: number;
}

export interface Vector2 {
  x: number;
  y: number;
}

// Addon Types //
export interface KeyPickerAddon {
  type: "KeyPicker";
  mode: "Toggle" | "Hold" | "Always";
  value: string;
  text: string;
}

export interface ColorPickerAddon {
  type: "ColorPicker";
  value: Color3;
  title: string;
}

export type Addons = KeyPickerAddon | ColorPickerAddon;

// Element Types //
interface BaseElement {
  index: number;
  visible: boolean;
  type: string;
  text: string;
  disabled: boolean;
}

export interface ToggleElement extends BaseElement {
  type: "Toggle";
  value: boolean;
  properties: {
    risky: boolean;
    addons?: Addons[] | undefined;
  };
}

export interface LabelElement extends BaseElement {
  type: "Label";
  properties: {
    doesWrap: boolean;
    addons?: Addons[] | undefined;
  };
}

export interface ButtonElement extends BaseElement {
  type: "Button";
  subButton?: {
    text: string;
  };
}

export interface DropdownElement extends BaseElement {
  type: "Dropdown";
  value: string | { [key: string]: boolean };
  properties: {
    values: string[];
    disabledValues: string[] | undefined;
    multi: boolean | undefined;
  };
}

export interface SliderElement extends BaseElement {
  type: "Slider";
  value: number;
  properties: {
    min: number;
    max: number;
    compact: boolean | undefined;
    rounding: number | undefined;
    prefix: string;
    suffix: string;
  };
}

export interface InputElement extends BaseElement {
  type: "Input";
  value: string;
  properties: {
    placeholder: string;
    finished: boolean;
    emptyReset: string;
    numeric: boolean;
    clearTextOnFocus: boolean;
    allowEmpty: boolean;
  };
}

export interface DividerElement extends BaseElement {
  type: "Divider";
  properties: object;
}

export interface ImageElement extends BaseElement {
  type: "Image";
  visible: boolean;
  properties: {
    image: string;
    color: Color3;
    rectOffset: Vector2;
    rectSize: Vector2;
    height: number;
    scaleType: string;
    transparency: number;
  };
}

export type UIElement =
  | ToggleElement
  | LabelElement
  | ButtonElement
  | DropdownElement
  | SliderElement
  | InputElement
  | DividerElement
  | ImageElement;

// JSON File Types //
export interface GroupboxData {
  name: string;
  order: number;
  side: "Left" | "Right" | "Unknown";
  elements: UIElement[];
}

export interface TabboxTab {
  type: "Tab";
  name: string;
  order: number;
  elements: UIElement[];
}

export interface TabboxData {
  type: "Tabbox";
  name: string;
  side: "Left" | "Right" | "Unknown";
  tabs: {
    [key: string]: TabboxTab;
  };
}

export interface TabData {
  name: string;
  type: string;
  icon: string;
  order: number;
  tabboxes: {
    Left: TabboxData[];
    Right: TabboxData[];
    Unknown: TabboxData[];
  };
  groupboxes: {
    Left: { [key: string]: GroupboxData };
    Right: { [key: string]: GroupboxData };
    Unknown: { [key: string]: GroupboxData };
  };
  warningBox: {
    Visible: boolean;
    Title: string;
    IsNormal: boolean;
    Text: string;
    LockSize: boolean;
  };
}

export interface UIData {
  tabs: {
    [key: string]: TabData;
  };
}

import { IBM_Plex_Mono } from "next/font/google";
export const IBMMono = IBM_Plex_Mono({
	weight: ["400"],
	subsets: ["latin"],
	variable: "--font",
});

"use client";

import React from "react";
import type { ReactNode } from "react";

import type {
  UIData,
  TabData,
  GroupboxData,
  UIElement,
  Color3,
  Vector2,
  TabboxData,
} from "./element.types";
import { UIStateProvider } from "./UIStateProvider";
import { ObsidianWindow } from "./Window";

// Root wrapper props
type ObsidianWrapperProps = {
  title: ReactNode | string;
  icon?: ReactNode | string;
  footer: ReactNode | string;
  uiData?: unknown;
  children?: ReactNode;
  width?: number | string;
  height?: number | string;
};

// DSL component prop types
export type ObsidianTabProps = {
  name: string;
  order?: number;
  icon?: string;
  children?: ReactNode;
};

export type ObsidianSideProps = { children?: ReactNode };

export type ObsidianGroupboxProps = {
  name: string;
  order?: number;
  children?: ReactNode;
};

export type OToggleProps = {
  text: string;
  defaultChecked?: boolean;
  risky?: boolean;
  visible?: boolean;
  disabled?: boolean;
};

export type OLabelProps = {
  text: string;
  doesWrap?: boolean;
  visible?: boolean;
  disabled?: boolean;
};

export type OButtonProps = {
  text: string;
  visible?: boolean;
  disabled?: boolean;
  subButtonText?: string;
};

export type ODropdownProps = {
  text: string;
  value?: string | { [key: string]: boolean };
  options: string[];
  disabledValues?: string[];
  multi?: boolean;
  visible?: boolean;
  disabled?: boolean;
};

export type OSliderProps = {
  text: string;
  value?: number;
  min?: number;
  max?: number;
  compact?: boolean;
  rounding?: number;
  prefix?: string;
  suffix?: string;
  visible?: boolean;
  disabled?: boolean;
};

export type OInputProps = {
  text: string;
  value?: string;
  placeholder?: string;
  visible?: boolean;
  disabled?: boolean;
};

export type ODividerProps = {
  visible?: boolean;
  disabled?: boolean;
};

export type OImageProps = {
  text?: string;
  image: string;
  color?: Color3;
  rectOffset?: Vector2;
  rectSize?: Vector2;
  height?: number;
  scaleType?: string;
  transparency?: number;
  visible?: boolean;
  disabled?: boolean;
};

export type TabWarningProps = {
  visible?: boolean;
  title?: string;
  text?: string;
  isNormal?: boolean;
  lockSize?: boolean;
};

// DSL marker components (return null; used only for structure parsing)
const TAGS = {
  Tab: "obsidian-tab",
  Left: "obsidian-left",
  Right: "obsidian-right",
  Groupbox: "obsidian-groupbox",
  Toggle: "obsidian-toggle",
  Label: "obsidian-label",
  Button: "obsidian-button",
  Dropdown: "obsidian-dropdown",
  Slider: "obsidian-slider",
  Input: "obsidian-input",
  Divider: "obsidian-divider",
  Image: "obsidian-image",
  TabWarning: "obsidian-tabwarning",
} as const;

// Strongly-typed, no-op marker components used for structural parsing
const makeMarker = <P extends object>(display: string) => {
  const Marker: React.FC<P> = () => null;
  Marker.displayName = display;
  return Marker;
};

export const ObsidianTab = makeMarker<ObsidianTabProps>("ObsidianTab");
export const ObsidianLeft = makeMarker<ObsidianSideProps>("ObsidianLeft");
export const ObsidianRight = makeMarker<ObsidianSideProps>("ObsidianRight");
export const ObsidianGroupbox = makeMarker<ObsidianGroupboxProps>(
  "ObsidianGroupbox"
);

export const OToggle = makeMarker<OToggleProps>("OToggle");
export const OLabel = makeMarker<OLabelProps>("OLabel");
export const OButton = makeMarker<OButtonProps>("OButton");
export const ODropdown = makeMarker<ODropdownProps>("ODropdown");
export const OSlider = makeMarker<OSliderProps>("OSlider");
export const OInput = makeMarker<OInputProps>("OInput");
export const ODivider = makeMarker<ODividerProps>("ODivider");
export const OImage = makeMarker<OImageProps>("OImage");
export const TabWarning = makeMarker<TabWarningProps>("TabWarning");

// Long-form aliases for convenience in pages
export { OLabel as ObsidianLabel };
export { OToggle as ObsidianToggle };
export { OButton as ObsidianButton };
export { ODropdown as ObsidianDropdown };
export { OSlider as ObsidianSlider };
export { OInput as ObsidianInput };
export { ODivider as ObsidianDivider };
export { OImage as ObsidianImage };
export { TabWarning as ObsidianTabWarning };

// Parsing helpers
const MARK = "__obsidianType";
type ElementLike = { type?: unknown };
function isTag(node: ReactNode, tag: string): node is React.ReactElement {
  return !!(node && typeof node === "object" && (node as ElementLike).type === tag);
}
function getExportNameFromClientRef(ref: unknown): string | undefined {
  // Next.js client references often have an $$id like "path/to/file.tsx#ExportName"
  const maybe = ref as { $$id?: unknown } | undefined;
  const id = maybe && typeof maybe.$$id === "string" ? maybe.$$id : undefined;
  if (!id) return undefined;
  const hash = id.lastIndexOf("#");
  return hash >= 0 ? id.slice(hash + 1) : undefined;
}

type NameLike = {
  displayName?: string;
  name?: string;
  _name?: string;
  render?: { displayName?: string; name?: string };
};
function getTypeName(t: unknown): string | undefined {
  const n = t as NameLike | undefined;
  return (
    n?.displayName ||
    n?.name ||
    n?._name ||
    n?.render?.displayName ||
    n?.render?.name ||
    getExportNameFromClientRef(t)
  );
}

function isElementOfType<T>(
  node: ReactNode,
  cmp: React.ComponentType<T>
): node is React.ReactElement<T> {
  if (!React.isValidElement(node)) return false;
  const t = node.type as unknown;
  if (t === cmp) return true;

  const tName = getTypeName(t);
  const cName = getTypeName(cmp);

  if (!tName || !cName) return false;

  // Allow matching "Obsidian.Tab" to "ObsidianTab" and vice versa.
  const normalize = (s: string) => s.replace(/\./g, "");
  return normalize(tName) === normalize(cName);
}

function isMarker(
  node: ReactNode,
  type: string
): node is React.ReactElement {
  if (!node || typeof node !== "object") return false;
  const el = node as { props?: Record<string, unknown> };
  return !!(el.props && el.props[MARK] === type);
}

function parseElements(
  children: ReactNode,
  startIndex = 0
): { elements: UIElement[]; lastIndex: number } {
  const elements: UIElement[] = [];
  let idx = startIndex;

  React.Children.forEach(children, (child) => {
    if (
      isElementOfType(child, OToggle) ||
      isMarker(child, "Toggle") ||
      isTag(child, TAGS.Toggle)
    ) {
      const p = child.props as OToggleProps;
      elements.push({
        index: idx++,
        visible: p.visible ?? true,
        type: "Toggle",
        text: p.text,
        disabled: p.disabled ?? false,
        value: p.defaultChecked ?? false,
        properties: { risky: p.risky ?? false },
      } as unknown as UIElement);
      return;
    }
    if (
      isElementOfType(child, OLabel) ||
      isMarker(child, "Label") ||
      isTag(child, TAGS.Label)
    ) {
      const p = child.props as OLabelProps;
      elements.push({
        index: idx++,
        visible: p.visible ?? true,
        type: "Label",
        text: p.text,
        disabled: p.disabled ?? false,
        properties: { doesWrap: p.doesWrap ?? false },
      } as unknown as UIElement);
      return;
    }
    if (
      isElementOfType(child, OButton) ||
      isMarker(child, "Button") ||
      isTag(child, TAGS.Button)
    ) {
      const p = child.props as OButtonProps;
      elements.push({
        index: idx++,
        visible: p.visible ?? true,
        type: "Button",
        text: p.text,
        disabled: p.disabled ?? false,
        subButton: p.subButtonText ? { text: p.subButtonText } : undefined,
      } as unknown as UIElement);
      return;
    }
    if (
      isElementOfType(child, ODropdown) ||
      isMarker(child, "Dropdown") ||
      isTag(child, TAGS.Dropdown)
    ) {
      const p = child.props as ODropdownProps;
      elements.push({
        index: idx++,
        visible: p.visible ?? true,
        type: "Dropdown",
        text: p.text,
        disabled: p.disabled ?? false,
        value: p.value ?? (p.multi ? {} : p.options?.[0] ?? ""),
        properties: {
          values: p.options ?? [],
          disabledValues: p.disabledValues ?? [],
          multi: p.multi ?? false,
        },
      } as unknown as UIElement);
      return;
    }
    if (
      isElementOfType(child, OSlider) ||
      isMarker(child, "Slider") ||
      isTag(child, TAGS.Slider)
    ) {
      const p = child.props as OSliderProps;
      const min = p.min ?? 0;
      const max = p.max ?? 100;
      elements.push({
        index: idx++,
        visible: p.visible ?? true,
        type: "Slider",
        text: p.text,
        disabled: p.disabled ?? false,
        value: p.value ?? min,
        properties: {
          min,
          max,
          compact: p.compact,
          rounding: p.rounding,
          prefix: p.prefix ?? "",
          suffix: p.suffix ?? "",
        },
      } as unknown as UIElement);
      return;
    }
    if (
      isElementOfType(child, OInput) ||
      isMarker(child, "Input") ||
      isTag(child, TAGS.Input)
    ) {
      const p = child.props as OInputProps;
      elements.push({
        index: idx++,
        visible: p.visible ?? true,
        type: "Input",
        text: p.text,
        disabled: p.disabled ?? false,
        value: p.value ?? "",
        properties: {
          placeholder: p.placeholder ?? "",
          finished: false,
          emptyReset: "",
          numeric: false,
          clearTextOnFocus: false,
          allowEmpty: true,
        },
      } as unknown as UIElement);
      return;
    }
    if (
      isElementOfType(child, ODivider) ||
      isMarker(child, "Divider") ||
      isTag(child, TAGS.Divider)
    ) {
      const p = child.props as ODividerProps;
      elements.push({
        index: idx++,
        visible: p.visible ?? true,
        type: "Divider",
        text: "",
        disabled: p.disabled ?? false,
        properties: {},
      } as unknown as UIElement);
      return;
    }
    if (
      isElementOfType(child, OImage) ||
      isMarker(child, "Image") ||
      isTag(child, TAGS.Image)
    ) {
      const p = child.props as OImageProps;
      elements.push({
        index: idx++,
        visible: p.visible ?? true,
        type: "Image",
        text: p.text ?? "",
        disabled: p.disabled ?? false,
        properties: {
          image: p.image,
          color: p.color ?? { r: 255, g: 255, b: 255 },
          rectOffset: p.rectOffset ?? { x: 0, y: 0 },
          rectSize: p.rectSize ?? { x: 0, y: 0 },
          height: p.height ?? 30,
          scaleType: p.scaleType ?? "Fit",
          transparency: p.transparency ?? 1,
        },
      } as unknown as UIElement);
      return;
    }
  });

  return { elements, lastIndex: idx };
}

function parseGroupboxes(children: ReactNode): {
  byName: { [key: string]: GroupboxData };
} {
  const out: { [key: string]: GroupboxData } = {};
  let globalIndex = 0;

  React.Children.forEach(children, (child) => {
    if (
      !isElementOfType(child, ObsidianGroupbox) &&
      !isMarker(child, "Groupbox") &&
      !isTag(child, TAGS.Groupbox)
    )
      return;
    const p = child.props as ObsidianGroupboxProps;

    const { elements } = parseElements(p.children, 0);
    const gb: GroupboxData = {
      name: p.name,
      order: p.order ?? 0,
      side: "Unknown",
      elements: elements.map((el) => ({
        ...el,
        index: globalIndex++,
      })) as unknown as UIElement[],
    };
    out[p.name] = gb;
  });

  return { byName: out };
}

function parseTabs(children: ReactNode): UIData {
  const tabs: { [key: string]: TabData } = {};

  React.Children.forEach(children, (child) => {
    if (
      !isElementOfType(child, ObsidianTab) &&
      !isMarker(child, "Tab") &&
      !isTag(child, TAGS.Tab)
    )
      return;
    const p = child.props as ObsidianTabProps;

    const leftNode = React.Children.toArray(p.children).find(
      (c) =>
        isElementOfType(c, ObsidianLeft) ||
        isMarker(c, "Left") ||
        isTag(c, TAGS.Left)
    ) as React.ReactElement | undefined;
    const rightNode = React.Children.toArray(p.children).find(
      (c) =>
        isElementOfType(c, ObsidianRight) ||
        isMarker(c, "Right") ||
        isTag(c, TAGS.Right)
    ) as React.ReactElement | undefined;
    const warningNode = React.Children.toArray(p.children).find(
      (c) =>
        isElementOfType(c, TabWarning) ||
        isMarker(c, "TabWarning") ||
        isTag(c, TAGS.TabWarning)
    ) as React.ReactElement | undefined;

    const left = leftNode
      ? parseGroupboxes(
          (leftNode.props as { children?: React.ReactNode }).children
        )
      : { byName: {} };
    const right = rightNode
      ? parseGroupboxes(
          (rightNode.props as { children?: React.ReactNode }).children
        )
      : { byName: {} };

    const tabData: TabData = {
      name: p.name,
      type: "Tab",
      icon: p.icon ?? "",
      order: p.order ?? 0,
      tabboxes: { Left: [], Right: [], Unknown: [] },
      groupboxes: {
        Left: left.byName,
        Right: right.byName,
        Unknown: {},
      },
      warningBox: {
        Visible: (warningNode?.props as TabWarningProps)?.visible ?? false,
        Title: (warningNode?.props as TabWarningProps)?.title ?? "",
        IsNormal: (warningNode?.props as TabWarningProps)?.isNormal ?? true,
        Text: (warningNode?.props as TabWarningProps)?.text ?? "",
        LockSize: (warningNode?.props as TabWarningProps)?.lockSize ?? false,
      },
    };

    tabs[p.name] = tabData;
  });

  return { tabs } as UIData;
}

function buildUIData(children?: ReactNode): UIData | undefined {
  if (!children) return undefined;
  return parseTabs(children);
}

// Accepts a variety of shapes and normalizes into UIData
function normalizeUIData(data: unknown): UIData | undefined {
  if (!data || typeof data !== "object") return undefined;
  const anyData = data as { tabs?: unknown };
  const tabsSrc =
    anyData.tabs && typeof anyData.tabs === "object"
      ? (anyData.tabs as Record<string, unknown>)
      : undefined;
  if (!tabsSrc) return undefined;

  const outTabs: Record<string, TabData> = {};
  for (const [name, raw] of Object.entries(tabsSrc as Record<string, unknown>)) {
    if (!raw || typeof raw !== "object") continue;

    type RawTab = {
      name?: string;
      type?: string;
      icon?: string;
      order?: unknown;
      tabboxes?: {
        Left?: unknown;
        Right?: unknown;
        Unknown?: unknown;
      };
      groupboxes?: {
        Left?: unknown;
        Right?: unknown;
        Unknown?: unknown;
      };
      warningBox?: {
        Visible?: unknown;
        Title?: unknown;
        IsNormal?: unknown;
        Text?: unknown;
        LockSize?: unknown;
      };
    };

    const normalizeGroupSide = (side: unknown): { [key: string]: GroupboxData } => {
      if (!side) return {};
      if (Array.isArray(side)) {
        const acc: { [key: string]: GroupboxData } = {};
        for (const gb of side as unknown[]) {
          const maybeGb = gb as Partial<GroupboxData> | undefined;
          if (maybeGb && maybeGb.name) acc[String(maybeGb.name)] = maybeGb as GroupboxData;
        }
        return acc;
      }
      if (typeof side === "object")
        return side as { [key: string]: GroupboxData };
      return {};
    };

    const normalizeTabSide = (side: unknown): TabboxData[] => {
      if (!side) return [];
      if (Array.isArray(side)) return side as TabboxData[];
      if (typeof side === "object")
        return Object.values(side as Record<string, TabboxData>);
      return [];
    };

    const rawTab = raw as RawTab;
    const tabboxes = rawTab.tabboxes ?? { Left: [], Right: [], Unknown: [] };
    const groupboxes = rawTab.groupboxes ?? { Left: {}, Right: {}, Unknown: {} };

    outTabs[name] = {
      name: (rawTab.name ?? name) as string,
      type: (rawTab.type ?? "Tab") as string,
      icon: (rawTab.icon ?? "") as string,
      order: Number(rawTab.order ?? 0),
      tabboxes: {
        Left: normalizeTabSide(tabboxes.Left),
        Right: normalizeTabSide(tabboxes.Right),
        Unknown: normalizeTabSide(tabboxes.Unknown),
      },
      groupboxes: {
        Left: normalizeGroupSide(groupboxes.Left),
        Right: normalizeGroupSide(groupboxes.Right),
        Unknown: normalizeGroupSide(groupboxes.Unknown),
      },
      warningBox: {
        Visible: Boolean(rawTab.warningBox?.Visible ?? false),
        Title: String(rawTab.warningBox?.Title ?? ""),
        IsNormal: Boolean(rawTab.warningBox?.IsNormal ?? true),
        Text: String(rawTab.warningBox?.Text ?? ""),
        LockSize: Boolean(rawTab.warningBox?.LockSize ?? false),
      },
    } as TabData;
  }

  return { tabs: outTabs } as UIData;
}

export function Obsidian({
  title,
  icon,
  footer,
  uiData,
  children,
  width,
  height,
}: ObsidianWrapperProps) {
  const data = React.useMemo(() => {
    const fromChildren = buildUIData(children);
    const fromProp = normalizeUIData(uiData);
    return fromProp ?? fromChildren;
  }, [uiData, children]);

  return (
    <UIStateProvider>
      <ObsidianWindow
        title={title}
        icon={icon}
        footer={footer}
        uiData={data}
        width={width}
        height={height}
      />
    </UIStateProvider>
  );
}

