export default function Input({
  text,
  value,
  placeholder,
  stateKey,
  className,
  containerClassName,
  inputClassName,
  onChanged,
}: {
  text: string;
  value: string;
  placeholder: string;
  stateKey?: string;
  className?: string;
  containerClassName?: string;
  inputClassName?: string;
  onChanged?: React.ChangeEventHandler<HTMLInputElement>;
}) {
  const { state, setState } = useUIState();
  const [local, setLocal] = React.useState<string>(
    (stateKey ? (state[stateKey] as string | undefined) : undefined) ?? value
  );

  React.useEffect(() => {
    if (!stateKey) return;
    const v = state[stateKey];
    if (typeof v === "string") setLocal(v);
  }, [state, stateKey]);

  // sync with external value when uncontrolled //
  React.useEffect(() => {
    if (stateKey) return;
    setLocal(value);
  }, [value, stateKey]);

  return (
    <div className="flex flex-col gap-1">
      <Label className="text-white opacity-100">{text}</Label>

      <ButtonBase
        text={
          <input
            name="input"
            type="text"
            className={cn(
              "w-full h-full text-white opacity-100 ml-1 text-xs bg-transparent outline-none",
              inputClassName
            )}
            value={local}
            placeholder={placeholder}
            onChange={(e) => {
              const next = e.target.value;
              setLocal(next);
              if (stateKey) setState(stateKey, next);
              if (onChanged) onChanged(e);
            }}
          />
        }
        className={cn(
          "text-left text-white opacity-100 m-1 text-xs",
          className
        )}
        containerClassName={cn(
          "justify-start flex relative",
          containerClassName
        )}
        replacedText={true}
      />
    </div>
  );
}

import { cn } from "@/lib/utils";
import DOMPurify from "dompurify";
import type { ReactNode, CSSProperties } from "react";

function escapeHtml(input: string) {
  return input
    .replace(/&/g, "&amp;")
    .replace(/</g, "&lt;")
    .replace(/>/g, "&gt;")
    .replace(/\"/g, "&quot;")
    .replace(/'/g, "&#39;");
}

function sanitizeColor(value: string): string | null {
  const v = value.trim();
  if (/^#([0-9a-fA-F]{3}|[0-9a-fA-F]{6})$/.test(v)) return v;

  const rgbMatch =
    /^rgb\(\s*(\d{1,3})\s*,\s*(\d{1,3})\s*,\s*(\d{1,3})\s*\)$/i.exec(v);
  if (rgbMatch) {
    const r = Number(rgbMatch[1]);
    const g = Number(rgbMatch[2]);
    const b = Number(rgbMatch[3]);
    if (r <= 255 && g <= 255 && b <= 255) {
      return `rgb(${r}, ${g}, ${b})`;
    }
  }

  const rgbaMatch =
    /^rgba\(\s*(\d{1,3})\s*,\s*(\d{1,3})\s*,\s*(\d{1,3})\s*,\s*(0|0?\.\d+|1(\.0+)?)\s*\)$/i.exec(
      v
    );
  if (rgbaMatch) {
    const r = Number(rgbaMatch[1]);
    const g = Number(rgbaMatch[2]);
    const b = Number(rgbaMatch[3]);
    const a = Number(rgbaMatch[4]);

    if (r <= 255 && g <= 255 && b <= 255 && a >= 0 && a <= 1) {
      const alpha = String(parseFloat(a.toString()));
      return `rgba(${r}, ${g}, ${b}, ${alpha})`;
    }
  }

  const triplet = /^(\d{1,3})\s*,\s*(\d{1,3})\s*,\s*(\d{1,3})$/.exec(v);
  if (triplet) {
    const r = Number(triplet[1]);
    const g = Number(triplet[2]);
    const b = Number(triplet[3]);
    if (r <= 255 && g <= 255 && b <= 255) {
      return `rgb(${r}, ${g}, ${b})`;
    }
  }

  return null;
}

function robloxRichTextToHtml(input: string): string {
  if (!input) return "";

  const placeholders: string[] = [];
  const replacements: string[] = [];
  const tokenSeed = `__RTX_${Date.now()}_${Math.random()
    .toString(36)
    .slice(2)}__`;
  const makePh = (html: string) => {
    const token = `${tokenSeed}${placeholders.length}__`;
    placeholders.push(token);
    replacements.push(html);
    return token;
  };
  let s = String(input);
  s = s.replace(/<\s*b\s*>/gi, () => makePh("<b>"));
  s = s.replace(/<\s*\/\s*b\s*>/gi, () => makePh("</b>"));
  s = s.replace(/<\s*i\s*>/gi, () => makePh("<i>"));
  s = s.replace(/<\s*\/\s*i\s*>/gi, () => makePh("</i>"));
  s = s.replace(/<\s*u\s*>/gi, () => makePh("<u>"));
  s = s.replace(/<\s*\/\s*u\s*>/gi, () => makePh("</u>"));
  s = s.replace(/<\s*s\s*>/gi, () => makePh("<s>"));
  s = s.replace(/<\s*\/\s*s\s*>/gi, () => makePh("</s>"));
  s = s.replace(/<\s*br\s*\/?\s*>/gi, () => makePh("<br/>"));

  s = s.replace(/<\s*font([^>]*)>/gi, (_m, attrs: string) => {
    let color: string | null = null;
    let size: number | null = null;

    const attrRegex = /(\w+)\s*=\s*("[^"]*"|'[^']*'|[^\s"'>]+)/g;
    let match: RegExpExecArray | null;
    while ((match = attrRegex.exec(attrs)) !== null) {
      const name = match[1].toLowerCase();
      let raw = match[2];
      if (
        (raw.startsWith('"') && raw.endsWith('"')) ||
        (raw.startsWith("'") && raw.endsWith("'"))
      ) {
        raw = raw.slice(1, -1);
      }
      if (name === "color") {
        const c = sanitizeColor(raw);
        if (c) color = c;
      } else if (name === "size") {
        const n = parseInt(raw, 10);
        if (!Number.isNaN(n)) {
          size = Math.min(Math.max(n, 6), 72);
        }
      }
    }

    const styles: string[] = [];
    if (color) styles.push(`color: ${color}`);
    if (size !== null) styles.push(`font-size: ${size}px`);
    const styleAttr = styles.length ? ` style=\"${styles.join("; ")}\"` : "";
    return makePh(`<span${styleAttr}>`);
  });

  s = s.replace(/<\s*\/\s*font\s*>/gi, () => makePh("</span>"));
  s = escapeHtml(s);
  s = s.replace(/\r\n|\n|\r/g, "<br/>");

  placeholders.forEach((ph, i) => {
    s = s.split(ph).join(replacements[i]);
  });

  return s;
}

export default function Label({
  children,
  className,
  style,
}: {
  children: ReactNode;
  className?: string;
  style?: CSSProperties;
}) {
  const finalClassName = cn("text-left block text-white text-sm", className);

  if (typeof children === "string") {
    const htmlUnsafe = robloxRichTextToHtml(children);
    const html = DOMPurify.sanitize(htmlUnsafe, {
      ALLOWED_TAGS: ["b", "i", "u", "s", "br", "span"],
      ALLOWED_ATTR: ["style"],
    });
    return (
      <span
        className={finalClassName}
        style={style}
        dangerouslySetInnerHTML={{ __html: html }}
      />
    );
  }

  return (
    <span className={finalClassName} style={style}>
      {children}
    </span>
  );
}

"use client";
import * as React from "react";

import { cn } from "@/lib/utils";
import { ButtonBase } from "./Button";
import Label from "./Label";
import { useUIValue } from "../UIStateProvider";

export default function Slider({
  text,
  value,
  defaultValue,
  min = 0,
  max = 100,
  step = 1,
  compact,
  rounding,
  prefix,
  suffix,
  className,
  onValueChange,
  stateKey,
}: {
  text: string;
  value?: number;
  defaultValue?: number;
  min?: number;
  max?: number;
  step?: number;
  compact?: boolean;
  rounding?: number;
  prefix?: string;
  suffix?: string;
  className?: string;
  onValueChange?: (value: number) => void;
  stateKey?: string;
}) {
  const [stateValue, setStateValue] = useUIValue<number | undefined>(
    stateKey,
    undefined
  );
  const [internalValue, setInternalValue] = React.useState(
    stateValue ?? value ?? defaultValue ?? min
  );

  const isControlled = value !== undefined && onValueChange !== undefined;
  const currentValue = isControlled ? (value as number) : internalValue;

  React.useEffect(() => {
    if (!isControlled && stateValue !== undefined) {
      setInternalValue(stateValue);
    }
  }, [stateValue, isControlled]);

  const roundValue = (num: number) => {
    if (rounding !== undefined && rounding >= 0) {
      const multiplier = Math.pow(10, rounding);
      return Math.round(num * multiplier) / multiplier;
    }
    return num;
  };

  const trackRef = React.useRef<HTMLDivElement | null>(null);
  const draggingRef = React.useRef(false);

  const quantizeToStep = (n: number) => {
    const range = max - min;
    if (step <= 0 || !Number.isFinite(step) || range === 0) return n;

    const roundingStep =
      rounding !== undefined && rounding >= 0 ? Math.pow(10, -rounding) : null;
    const effectiveStep = roundingStep ? Math.min(step, roundingStep) : step;
    const steps = Math.round((n - min) / effectiveStep);
    const snapped = min + steps * effectiveStep;

    return Math.min(max, Math.max(min, snapped));
  };

  const setFromClientX = (clientX: number) => {
    const el = trackRef.current;
    if (!el) return;

    const rect = el.getBoundingClientRect();
    const pct = rect.width > 0 ? (clientX - rect.left) / rect.width : 0;
    const clampedPct = Math.min(1, Math.max(0, pct));
    const raw = min + clampedPct * (max - min);
    const stepped = quantizeToStep(raw);
    const roundedValue = roundValue(stepped);

    if (!isControlled) setInternalValue(roundedValue);
    if (stateKey) setStateValue(roundedValue);
    if (onValueChange) onValueChange(roundedValue);
  };

  const onPointerDown = (e: React.PointerEvent<HTMLDivElement>) => {
    e.preventDefault();
    draggingRef.current = true;
    try {
      (e.currentTarget as HTMLDivElement).setPointerCapture(e.pointerId);
    } catch {}
    setFromClientX(e.clientX);

    const move = (ev: PointerEvent) => {
      if (!draggingRef.current) return;
      setFromClientX(ev.clientX);
    };

    const up = () => {
      draggingRef.current = false;
      window.removeEventListener("pointermove", move);
      window.removeEventListener("pointerup", up);
      window.removeEventListener("pointercancel", up);
    };

    window.addEventListener("pointermove", move);
    window.addEventListener("pointerup", up);
    window.addEventListener("pointercancel", up);
  };

  const displayValue = roundValue(currentValue);
  const displayMax = roundValue(max);
  const formatValue = (num: number) =>
    rounding !== undefined && rounding >= 0
      ? Number(num).toFixed(rounding)
      : String(num);

  const fraction = max > min ? (currentValue - min) / (max - min) : 0;
  const percent = Math.min(100, Math.max(0, fraction * 100));

  if (compact) {
    const sliderText = `${prefix || ""}${text}${suffix || ""}: ${formatValue(
      displayValue as number
    )}`;

    return (
      <ButtonBase
        text=""
        replacedText={true}
        containerClassName={cn(className, "h-[20px]")}
      >
        <div className="relative w-full h-full flex items-center justify-center">
          <div
            ref={trackRef}
            role="slider"
            aria-valuemin={min}
            aria-valuemax={max}
            aria-valuenow={displayValue}
            className="absolute inset-0 z-10 cursor-pointer overflow-hidden rounded-[1px] select-none touch-none pointer-events-auto"
            onPointerDown={onPointerDown}
            onPointerMove={(e) => {
              if (!draggingRef.current) return;
              setFromClientX(e.clientX);
            }}
          >
            <div
              className="absolute left-0 top-0 h-full bg-[rgb(125,85,255)]"
              style={{ width: `${percent}%` }}
            />
          </div>
          <span
            className={cn(
              `text-center text-white text-sm truncate z-30 pointer-events-none`,
              "select-none"
            )}
            style={{
              WebkitTextStroke: "0.1px #000",
              WebkitTextFillColor: "white",
              textShadow:
                "0 1px 0 #000, 1px 0 0 #000, 0 -1px 0 #000, -1px 0 0 #000",
            }}
          >
            {sliderText}
          </span>
        </div>
      </ButtonBase>
    );
  }

  return (
    <div className={cn("flex flex-col gap-1", className)}>
      <Label className="text-white opacity-100">{text}</Label>
      <div className="relative w-full h-[20px] rounded-[1px] bg-[rgb(25,25,25)] border-[rgb(40,40,40)] border">
        <div
          ref={trackRef}
          role="slider"
          aria-valuemin={min}
          aria-valuemax={max}
          aria-valuenow={displayValue}
          className="absolute inset-0 cursor-pointer overflow-hidden rounded-[1px] select-none touch-none"
          onPointerDown={onPointerDown}
          onPointerMove={(e) => {
            if (!draggingRef.current) return;
            setFromClientX(e.clientX);
          }}
        >
          <div className="absolute inset-0" />
          <div
            className="absolute left-0 top-0 h-full bg-[rgb(125,85,255)]"
            style={{ width: `${percent}%` }}
          />
        </div>
        <div className="absolute inset-0 flex items-center justify-center pointer-events-none">
          <span
            className="text-center text-white text-sm select-none"
            style={{
              WebkitTextStroke: "0.1px #000",
              WebkitTextFillColor: "white",
              textShadow:
                "0 1px 0 #000, 1px 0 0 #000, 0 -1px 0 #000, -1px 0 0 #000",
            }}
          >
            {formatValue(displayValue as number)}/
            {formatValue(displayMax as number)}
          </span>
        </div>
      </div>
    </div>
  );
}

