"use client";

import { useState, useMemo, useEffect } from "react";
import { ElementParser } from "../DynamicTab";
import { TabboxTab } from "../element.types";

export default function Tabbox({
  tabs,
  scope,
}: {
  tabs: { [key: string]: TabboxTab };
  scope: string;
}) {
  const tabNames = useMemo(
    () =>
      Object.keys(tabs).sort(
        (a, b) => (tabs[a]?.order ?? 0) - (tabs[b]?.order ?? 0)
      ),
    [tabs]
  );

  const [activeTab, setActiveTab] = useState(tabNames[0]);
  const activeTabData = useMemo(() => tabs[activeTab], [tabs, activeTab]);
  useEffect(() => {
    if (tabNames.length && (!activeTab || !tabs[activeTab])) {
      setActiveTab(tabNames[0]);
    }
  }, [tabNames, tabs, activeTab]);

  if (tabNames.length === 0) return null;
  return (
    <div className="mt-1 ml-2 mb-3 rounded-[3px] bg-[rgb(15,15,15)] border border-[rgb(40,40,40)] relative font-normal">
      <div className="w-full h-[38px] flex flex-row bg-[rgb(15,15,15)]">
        {/* Buttons */}
        <div className="flex flex-row items-center w-full border-b border-b-[rgb(40,40,40)]">
          {tabNames &&
            tabNames.map((name) => (
              <button
                key={name}
                onClick={() => setActiveTab(name)}
                className={`flex-1 h-full text-[13px] ${
                  activeTab === name
                    ? "text-white"
                    : "bg-[rgb(40,40,40)] text-white opacity-50"
                }`}
              >
                {name}
              </button>
            ))}
        </div>
      </div>

      {/* Content */}
      <div className="flex flex-col right p-2 gap-2">
        {activeTabData?.elements?.map((el) => (
          <ElementParser
            key={`${activeTab}-${el.index}`}
            element={el}
            stateKeyPrefix={`${scope}:tab:${activeTab}`}
          />
        ))}
      </div>
    </div>
  );
}

"use client";

import { CheckIcon } from "lucide-react";
import { useEffect, useState } from "react";
import { useUIValue } from "../UIStateProvider";

export default function Toggle({
  text,
  checked,
  risky,
  stateKey,
}: {
  text: string;
  checked: boolean;
  risky: boolean;
  stateKey?: string;
}) {
  const [externalChecked, setExternalChecked] = useUIValue<boolean | undefined>(
    stateKey,
    undefined
  );
  const [isChecked, setChecked] = useState<boolean>(
    (externalChecked as boolean | undefined) ?? checked
  );

  useEffect(() => {
    if (!stateKey) return;
    const v = externalChecked;
    if (typeof v === "boolean") setChecked(v);
  }, [externalChecked, stateKey]);

  return (
    <div
      className="relative gap-2"
      onClick={(e) => {
        e.preventDefault();
        const next = !isChecked;
        setChecked(next);
        if (stateKey) setExternalChecked(next);
      }}
    >
      <button
        type="button"
        className="absolute left-0 w-[22px] h-[22px] rounded-[3px] bg-[rgb(25,25,25)] hover:bg-[rgb(35,35,35)] border-[rgb(40,40,40)] border"
      >
        <CheckIcon
          className={`w-[16px] h-[16px] m-[2px] transition-opacity stroke-white`}
          style={{ opacity: isChecked == true ? 1 : 0 }}
        />
      </button>

      <span
        className={`ml-[28px] text-left block text-sm select-none opacity-${
          isChecked == true ? "80" : "60"
        } transition-opacity ${risky ? "text-red-500" : "text-white"}`}
      >
        {text}
      </span>
    </div>
  );
}

